import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

export default function Details() {
  const { id } = useParams();
  const [produit, setProduit] = useState("");

  useEffect(() => {
    fetch(`/annonce/:${id}`, {
      method: "GET",
    })
      .then((data) => data.json())
      .then((resultat) => {
        setProduit(resultat.data);
        console.log(resultat);
      })
      .catch((err) => console.log(err));
  });

  return (
    <div>
      <h1>Page de d√©tails</h1>
      <div className="containerProduit">
        {(produit = { photo, nom, prix, quantity, categorie, description })}
        <div key={id} className="produit">
          <img src={photo} alt="produit" className="imgProduit" />
          <h2>{produit.nom}</h2>
          <p>{produit.prix} $</p>
          <p>{produit.description}</p>
          <h3>{produit.categorie}</h3>
          <p>{produit.quantity}</p>
        </div>
      </div>
    </div>
  );
}
