import React, { useEffect, useContext, useState } from "react";
import { state } from "../Context";

export default function Panier() {
  let { stateNavBar } = useContext(state);
  const [listProduit, setListProduit] = useState([]);

  useEffect(() => {
    fetch("panier", {
      headers: { Authorization: `Bearer ${stateNavBar}` },
    })
      .then((data) => data.json())
      .then((resultat) => {
        const listID = resultat.data;
        listID.forEach((id) => {
          fetch(`/panier/${id}`)
            .then((data) => data.json())
            .then((produit) =>
              setListProduit((prev) => [produit.data, ...prev])
            )
            .catch((err) => console.log(err));
        });
      })
      .catch((erreur) => console.log(erreur));
  }),
    [stateNavBar];
  return (
    <div>
      <h1>Page Panier</h1>
      <div className="containerDeProduit">
        <img src={produit.photo} alt="produit" className="imgDeProduit" />
        <div key={id} className="deProduit">
          <p id="id">ID: {id} $</p>
          <h2 id="nom">{produit.nom}</h2>
          <p>Prix: {produit.prix} $</p>
          <p>Cat√©gorie: {produit.categorie}</p>
          <p>Quantity: {produit.quantity}</p>
          <p>{produit.description}</p>
        </div>
      </div>
    </div>
  );
}
