import React, { useState } from "react";
import "./styles/Connexion.css";

export default function Annonces() {
  const [nom, setNom] = useState("");
  const [prix, setPrix] = useState("");
  const [categorie, setCategorie] = useState("");
  const [quantity, setQuantity] = useState("");
  const [description, setDescription] = useState("");
  const [photo, setPhoto] = useState("");

  const enregistrement = () => {};
  return (
    <div id="container">
      <form id="form" onSubmit={enregistrement}>
        <h1>Inscription</h1>
        <label className="labels">Nom</label>
        <input
          id="nom"
          type="text"
          placeholder="Entrez le nom du produit"
          value={nom}
          onChange={(event) => setNom(event.target.value)}
        />
        <label className="labels">Prix</label>
        <input
          id="prix"
          type="text"
          placeholder="Entrez le prix du produit"
          value={prix}
          onChange={(event) => setPrix(event.target.value)}
        />
        <label className="labels">Description</label>
        <input
          id="description"
          type="text"
          placeholder="Entrez la description du produit"
          value={description}
          onChange={(event) => setDescription(event.target.value)}
        />
        <label className="labels">Categorie</label>
        <input
          id="categorie"
          type="text"
          placeholder="Entrez la categorie du produit"
          value={categorie}
          onChange={(event) => setCategorie(event.target.value)}
        />
        <label className="labels">Quantité</label>
        <input
          id="quantity"
          type="number"
          placeholder="Entrez la quantité du produit"
          value={quantity}
          onChange={(event) => setQuantity(event.target.value)}
        />
        <label className="labels">Image</label>
        <input
          id="image"
          type="image"
          placeholder="Importez l'image du produit"
          alt=""
          value={photo}
          onChange={(event) => setPhoto(event.target.value)}
        />
        <input type="file" id="file" />
        <input id="submit" type="submit" value="Ajouter" />
      </form>
    </div>
  );
}
