import React, { useState } from "react";
import { useHistory } from "react-router";
import "./Connexion.css";

export default function Inscription() {
  const [nom, setNom] = useState("");
  const [prenom, setPrenom] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [photo, setPhoto] = useState("");

  const history = useHistory();

  const inscription = (event) => {
    event.preventDefault();
    let user = { nom, prenom, email, password };
    fetch("http://localhost:5000/user/signup", {
      method: "POST",
      body: JSON.stringify(user),
      header: { "Content-Type": "application/json" },
    })
      .then((data) => data.json())
      .then((resultat) => console.log(resultat))
      .catch((err) => console.log(err));
  };

  return (
    <div id="container">
      <form id="form" onSubmit={inscription}>
        <h1>Inscription</h1>
        <label className="labels">Nom</label>
        <input
          id="nom"
          type="text"
          placeholder="Entrez votre nom"
          value={nom}
          onChange={(event) => setNom(event.value.nom)}
        />
        <label className="labels">Prenom</label>
        <input
          id="prenom"
          type="text"
          placeholder="Entrez votre prÃ©nom"
          value={prenom}
          onChange={(event) => setPrenom(event.value.prenom)}
        />
        <label className="labels">Email</label>
        <input
          id="email"
          type="email"
          placeholder="Entrez votre email"
          value={email}
          onChange={(event) => setEmail(event.value.email)}
        />
        <label className="labels">Password</label>
        <input
          id="password"
          type="password"
          placeholder="Entrez votre mot de passe"
          value={password}
          onChange={(event) => setPassword(event.value.password)}
        />
        <label className="labels">Image</label>
        <input
          id="image"
          type="image"
          placeholder="Importez votre photo"
          alt=""
          value={photo}
          onChange={(event) => setPhoto(event.value.photo)}
        />
        <input type="file" id="file" />
        <input
          id="submit"
          type="submit"
          value="Inscription"
          onClick={() => history.push("/accueil")}
        />
      </form>
    </div>
  );
}
